import { IRouter } from 'express';
export { Request, Response } from 'express'
interface Router extends IRouter {
    invoke: () => Promise<{body: any, status: number}>
}

export interface API {
    get: Router['get']
    post: Router['post']
    put: Router['put']
    delete: Router['delete']
    patch: Router['patch']
    options: Router['options']
    head: Router['head']
    use: Router['use']
    static?: (directory: string) => void;
    reset?: () => void;
}

declare const api: API;
export default api;
