'use strict'

const User = require('../models/User')

module.exports = async (cli) => {
  cli.viewSpinner('Logging out')

  const user = new User()

  if (user.isLoggedIn()) {
    user.logout()
    cli.logSuccess('Successfully logged out of Serverless Cloud.')
  } else {
    cli.logInfo('You are already logged out of Serverless Cloud.')
  }
}
